//using static System.Net.Mime.MediaTypeNames;
using System.Diagnostics.Metrics;
using System.Drawing;
using CommonHelper;
using CommonHelper.Captcha;

namespace CommentPlugin_v2.CommonHelper.Captcha
{
    public class Captcha
    {
        public static Tuple<string, string> GetCaptcha()
        {


            char[] Valichars = {'1','2','3','4','5','6','7','8','9','a','b','c','d','e','f','g','h',
                           'j','k','l','m','n','p','q','r','s','t','u','v','w','x','y','z' };
            string Captcha = "";
            int LetterCount = 5;
            for (int i = 0; i < LetterCount; i++)
            {
                char newChar = (char)0;
                do
                {
                    newChar = char.ToUpper(Valichars[new Random(DateTime.Now.Millisecond).Next(Valichars.Count() - 1)]);
                }
                while (Captcha.Contains(newChar));
                Captcha += newChar;
            }

            var content = Crypton.Md5Encrypt(Crypton.Encrypt(Captcha.ToLower()));
            var imgbase = "";
            var CaptchaText = Captcha;
            if (CaptchaText != null)
            {
                List<Letter> letter = new List<Letter>();
                int TotalWidth = 0;
                int MaxHeight = 0;
                foreach (char c in CaptchaText)
                {
                    var ltr = new Letter(c);
                    letter.Add(ltr);
                    int space = new Random().Next(5) + 1;
                    ltr.space = space;
                    Thread.Sleep(1);
                    TotalWidth += ltr.LetterSize.Width + space;
                    if (MaxHeight < ltr.LetterSize.Height)
                        MaxHeight = ltr.LetterSize.Height;
                    Thread.Sleep(1);
                }
                const int HMargin = 5;
                const int VMargin = 1;
                if (TotalWidth < 140)
                    TotalWidth = 140;
                Bitmap bmp = new Bitmap(TotalWidth + HMargin, MaxHeight + VMargin);
                var Grph = Graphics.FromImage(bmp);
                Grph.FillRectangle(new SolidBrush(Color.Transparent), 0, 0, bmp.Width, bmp.Height);
                Pixelate(ref bmp);
                Grph.CompositingQuality = System.Drawing.Drawing2D.CompositingQuality.HighQuality;
                Grph.SmoothingMode = System.Drawing.Drawing2D.SmoothingMode.HighQuality;
                int xPos = HMargin;
                foreach (var ltr in letter)
                {
                    Grph.DrawString(ltr.letter.ToString(), ltr.font, new SolidBrush(Color.WhiteSmoke), xPos, VMargin);
                    xPos += ltr.LetterSize.Width + ltr.space;
                }
                Grph.Dispose();

                //Để tránh lỗi GDI+ (IIS 7, 7.5) thì phải save ảnh vào memory stream rồi response xuống
                //PNGs (and other formats) need to be saved to a seekable stream. Using an intermediate MemoryStream will do the trick:
                //https://stackoverflow.com/questions/5629251/c-sharp-outputting-image-to-response-output-stream-giving-gdi-error
                using (MemoryStream ms = new MemoryStream())
                {
                    bmp.Save(ms, System.Drawing.Imaging.ImageFormat.Png);
                    bmp.Dispose();

                    imgbase = "data:image/png;base64," + Convert.ToBase64String(ms.GetBuffer());
                }
            }
            return new Tuple<string, string>(content, imgbase);

        }
        private static void Pixelate(ref Bitmap bmp)
        {
            string[] imgs = {
                 " ",
                "",
                "",
                "",
                ""
            };

            var img = imgs[new Random().Next(0, imgs.Length)];
            using (var grp = Graphics.FromImage(bmp))
            {
                byte[] imageBytes = Convert.FromBase64String(img);
                using (var ms = new MemoryStream(imageBytes, 0, imageBytes.Length))
                {
                    Image background = Image.FromStream(ms, true);
                    grp.DrawImage(background, new Rectangle(0, 0, bmp.Width, bmp.Height));
                }
                //using (var background = Image.FromFile(HttpContext.Current.Server.MapPath(img)))
                //{
                //    grp.DrawImage(background, new Rectangle(0, 0, bmp.Width, bmp.Height));
                //}
            }
        }

    }
}
